
public class If_else_learn {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		
		int i=20;
		
		i--;
		
		System.out.println("value of i is ---> "+i);
		int a=i;
		
		System.out.println("value of a with one ++"+ a);
		
		i++;
		a=i;
		System.out.println("value of i in the line 20---> "+i);
	
		System.out.println("value of a with 2 ++--->"+a);
		
		System.out.println("value of a is--->"+a);
		
		
		System.out.println(i);
		
		
		if (i<20)
		{
			System.out.println("i value is lessthan or equal 20");
		}
		else if (i>20)
		{
			System.out.println("i value is greater than 20");
		}
		
		else if (i==20)
		{
			System.out.println("i value is exactly 20");
		}
		
		else
		{
			System.out.println("this is else block");
		}
		
		
		
		
		
		
		
		
		
		
		
		
		
		
	}

}
